import { Component, Input, OnInit } from '@angular/core';
import { SubmissionCardsComponent } from '../submission-cards/submission-cards.component';

@Component({
  selector: 'app-filter-bar',
  templateUrl: './filter-bar.component.html',
  styleUrls: ['./filter-bar.component.css'],
})
export class FilterBarComponent implements OnInit {
  @Input() cards: SubmissionCardsComponent = new SubmissionCardsComponent(); // Provide a default value


  searchTerm: string = '';
  selectedTitle: string = '';
  selectedStatus: string = '';
  selectedDate: string = '';

  uniqueTitles: string[] = [];
  uniqueStatus: string[] = [];

  ngOnInit(): void {
    // Populate uniqueTitles and uniqueStatus from cards data
    this.uniqueTitles = [...new Set(this.cards.submissionCards.map((card) => card.title))];
    this.uniqueStatus = [...new Set(this.cards.submissionCards.map((card) => card.status))];
  }
}
