// submission-cards.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-submission-cards',
  templateUrl: './submission-cards.component.html',
  styleUrls: ['./submission-cards.component.css']
})
export class SubmissionCardsComponent {
  today: Date = new Date(); // Get today's date

  submissionCards = [
    {
      title: 'Work Flow: Requires Location',
      from: 'john@example.com',
      to: 'alice@example.com',
      dueDate: new Date('2023-12-20'),
      status: 'completed'
    },
    {
      title: 'Work Flow: Requires Location',
      from: 'bob@example.com',
      to: 'carol@example.com',
      dueDate: new Date('2023-12-18'),
      status: 'not-completed'
    },
    {
      title: 'Work Flow: Requires Location',
      from: 'dave@example.com',
      to: 'eve@example.com',
      dueDate: new Date('2023-12-22'),
      status: 'needs-review'
    },
    {
      title: 'Work Flow: Requires Location',
      from: 'frank@example.com',
      to: 'grace@example.com',
      dueDate: new Date('2023-12-16'),
      status: 'not-completed'
    }
  ];
}
