import { Component, ElementRef, NgZone, OnInit, ViewChild } from '@angular/core';

@Component({
  selector: 'app-map-view',
  templateUrl: './map-view.component.html',
  styleUrls: ['./map-view.component.css']
})
export class MapViewComponent implements OnInit {
  @ViewChild('mapContainer', { static: false }) mapContainer: ElementRef | undefined;

  constructor(private zone: NgZone) {}

  ngOnInit(): void {
    // Don't call initMap here to avoid race conditions
    // Call it in the callback function specified in the script tag in index.html
  }

  initMap(): void {
    if (this.mapContainer) {
      const nativeElement = this.mapContainer.nativeElement;

      if (nativeElement instanceof HTMLElement) {
        this.zone.runOutsideAngular(() => {
          const map = new google.maps.Map(nativeElement, {
            center: { lat: 43.6532, lng: -79.3832 },
            zoom: 8
          });

          // Add markers or other map configurations as needed
        });
      } else {
        console.error('mapContainer.nativeElement is not an HTMLElement');
      }
    } else {
      console.error('mapContainer is undefined');
    }
  }

}
