// map-view.component.ts

import { AfterViewInit, Component, ElementRef, NgZone, ViewChild } from '@angular/core';

@Component({
  selector: 'app-map-view',
  template: '<div #mapContainer style="height: 400px;"></div>',
})
export class MapViewComponent implements AfterViewInit {
  @ViewChild('mapContainer', { static: false }) mapContainer: ElementRef;

  map: google.maps.Map;
  lat = 51.678418;
  lng = 7.809007;

  constructor(private zone: NgZone) {}

  ngAfterViewInit() {
    this.loadMap();
  }

  loadMap() {
    // Ensure that the Google Maps API is loaded
    if (typeof google === 'undefined' || !google.maps) {
      return;
    }

    const mapOptions: google.maps.MapOptions = {
      center: new google.maps.LatLng(this.lat, this.lng),
      zoom: 12,
    };

    // Run the map creation code inside the Angular zone
    this.zone.run(() => {
      this.map = new google.maps.Map(this.mapContainer.nativeElement, mapOptions);

      // Add markers, polygons, or other map features as needed.
    });
  }
}
