// map-view.component.ts
import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import * as L from 'leaflet';

@Component({
  selector: 'app-map-view',
  templateUrl: './map-view.component.html',
  styleUrls: ['./map-view.component.css'],
})
export class MapViewComponent implements OnChanges {
  @Input() filteredCards: any[] = [];

  private map: any;

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.filteredCards && changes.filteredCards.currentValue) {
      this.updateMap();
    }
  }

  private updateMap(): void {
    // Clear previous markers
    if (this.map) {
      this.map.eachLayer((layer: any) => {
        if (layer instanceof L.Marker) {
          this.map.removeLayer(layer);
        }
      });
    }

    // Create new markers for filtered cards
    this.filteredCards.forEach((card) => {
      const { latitude, longitude } = card.location; // Assuming each card has a 'location' property with 'latitude' and 'longitude'
      const marker = L.marker([latitude, longitude]).addTo(this.map);

      // You can customize the marker, add popups, etc. here
      marker.bindPopup(`<b>${card.title}</b><br>${card.from}`);
    });
  }

  ngAfterViewInit(): void {
    // Initialize the map
    this.map = L.map('leaflet-map').setView([0, 0], 2);

    // Add a tile layer (you can choose other providers)
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors',
    }).addTo(this.map);
  }
}
