// filter-bar.component.ts
import { Component, Input, OnInit, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-filter-bar',
  templateUrl: './filter-bar.component.html',
  styleUrls: ['./filter-bar.component.css'],
})
export class FilterBarComponent implements OnInit {
  @Input() cards: any[] = []; // Change the type to an array of cards
  @Output() filteredData = new EventEmitter<any[]>();

  searchTerm: string = '';
  selectedTitle: string = '';
  selectedStatus: string = '';
  selectedDate: string = '';

  uniqueTitles: string[] = [];
  uniqueStatus: string[] = [];
  filteredCards: any[] = [];

  ngOnInit(): void {
    // Populate uniqueTitles and uniqueStatus from cards data
    this.uniqueTitles = [...new Set(this.cards.map((card) => card.title))];
    this.uniqueStatus = [...new Set(this.cards.map((card) => card.status))];
    this.filteredCards = this.cards; // Initialize filteredCards with all cards
  }

  mapView: boolean = true; // Use a simple property

  toggleMapView(): void {
    this.mapView = !this.mapView;
    this.applyFilters();
  }

  applyFilters(): void {
    // Apply filters based on selected criteria
    this.filteredCards = this.cards
      .filter((card) =>
        card.title.toLowerCase().includes(this.selectedTitle.toLowerCase()) ||
        card.from.toLowerCase().includes(this.searchTerm.toLowerCase())
      )
      .filter((card) => !this.selectedStatus || card.status === this.selectedStatus)
      .filter((card) => !this.selectedDate || new Date(card.dueDate).toDateString() === new Date(this.selectedDate).toDateString());

    // Emit the filtered data to the parent component
    this.filteredData.emit(this.filteredCards);
  }

  exportToHTML(): void {
    // Create a simple HTML table
    const table = `
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>From</th>
            <th>To</th>
            <th>Due Date</th>
            <th>Status</th>
            <th>Text</th>
          </tr>
        </thead>
        <tbody>
          ${this.filteredCards.map(card => `
            <tr>
              <td>${card.title}</td>
              <td>${card.from}</td>
              <td>${card.to}</td>
              <td>${card.dueDate}</td>
              <td>${card.status}</td>
              <td>${card.text}</td>
            </tr>
          `).join('')}
        </tbody>
      </table>
    `;

    // Create a Blob containing the HTML table
    const blob = new Blob([table], { type: 'text/html' });

    // Create a download link
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'exported_data.html';

    // Append the link to the body and trigger the click event
    document.body.appendChild(a);
    a.click();

    // Remove the link from the body
    document.body.removeChild(a);
  }
}
